SELECT * FROM (SELECT temp.*, ROWNUM r FROM (SELECT ( SELECT COUNT(*) FROM ( SELECT gml.m_id FROM MEMBERS mm JOIN EMP_GROUP eg ON eg.m_id=mm.m_id JOIN group_m_list gml ON eg.group_no=gml.group_no WHERE mm.m_id=#{m_id} AND eg.group_name='즐겨찾기' AND eg.group_separator='기본그룹' ) book WHERE book.m_id=memb.m_id ) "mark", memb.m_profile_img "m_profile_img", memb.m_name "m_name", p.position_name "position_name", t.team_name "team_name", memb.m_id "m_id", memb.m_private_contact "m_private_contact", memb.m_hiredate "m_hiredate" FROM emp e JOIN members memb ON e.emp_code=memb.emp_code JOIN position p ON memb.position_code=p.position_code JOIN team t ON memb.team_no=t.team_no WHERE e.emp_code=#{emp_code} and m_id!=#{m_id}
AND ${searchColumn} LIKE '%' || #{searchWord} || '%'
ORDER BY "mark" DESC, "m_hiredate") temp) WHERE R BETWEEN #{start} AND #{end}

SELECT  dept_code "dept_code", dept_name "dept_name", 
	(SELECT m_id FROM members m JOIN team t ON m.team_no=t.team_no WHERE m_dept_leader IS NULL AND t.dept_code=d.dept_code
    ) "dept_leader_id", 
    (SELECT m_name FROM members mm JOIN team t ON mm.team_no=t.team_no WHERE m_dept_leader IS NULL AND t.dept_code=d.dept_code
    ) "dept_leader_name"
FROM dept d JOIN emp e ON d.emp_code=e.emp_code
WHERE e.emp_code=#{emp_code}
ORDER BY dept_name

SELECT tt.TEAM_NO "team_no", tt.TEAM_NAME "team_name", d.dept_code "dept_code" ,
	(SELECT m_id FROM members m JOIN team t ON m.team_no=t.team_no WHERE m_team_leader IS NULL AND t.team_no=tt.team_no
