    (SELECT m_name FROM members mm JOIN team t ON mm.team_no=t.team_no WHERE m_team_leader IS NULL AND t.team_no=tt.team_no
    ) "team_leader_name"
FROM team tt JOIN dept d ON tt.dept_code=d.dept_code
