mapper.select:
SELECT * FROM (SELECT temp.*, ROWNUM r FROM (SELECT ( SELECT COUNT(*) FROM ( SELECT gml.m_id FROM MEMBERS mm JOIN EMP_GROUP eg ON eg.m_id=mm.m_id JOIN group_m_list gml ON eg.group_no=gml.group_no WHERE mm.m_id=#{m_id} AND eg.group_name='즐겨찾기' AND eg.group_separator='기본그룹' ) book WHERE book.m_id=memb.m_id ) mark, memb.m_profile_img "m_profile_img", memb.m_name "m_name", p.position_name "position_name", t.team_name "team_name", memb.m_id "m_id", memb.m_private_contact "m_private_contact" FROM emp e JOIN members memb ON e.emp_code=memb.emp_code JOIN position p ON memb.position_code=p.position_code JOIN team t ON memb.team_no=t.team_no WHERE e.emp_code=#{emp_code} and m_id!=#{m_id}
AND ${searchColumn} LIKE '%' || #{searchWord} || '%'
ORDER BY mark DESC) temp) WHERE R BETWEEN #{start} AND #{end}
