mapper.select:
SELECT * FROM (SELECT group_no, group_name, m_id, m_name, count(group_no) AS group_members FROM (SELECT T.m_id, m.m_name, T.group_name, T.group_no FROM (SELECT gml.group_no, eg.group_name, eg.m_id, eg.group_separator FROM emp_group eg JOIN group_m_list gml ON gml.group_no = eg.group_no ORDER BY gml.group_no) T JOIN members m ON T.m_id = m.m_id) GROUP BY group_no, group_name,m_id, m_name ORDER BY group_no)
WHERE m_id IN
#{member}

SELECT m_id
FROM members 
WHERE emp_code = #{emp_code}
